if (block.type.startsWith("collection_view") && block.collection_id) {
    try {
      const collection = recordMap.collection[block.collection_id].value;
      block.format = {
        ...block.format,
        page_cover: collection.cover || undefined,
        page_icon: collection.icon,
      };
    } catch (e) {
      console.error(e);
    }
  }

if (recordMap.collection && recordMap.collection[block.parent_id]) {
      const collection = recordMap.collection[block.parent_id].value;
      breadcrumbs.unshift({
        block: recordMap.block[collection.parent_id].value,
        active: false,
        pageId: collection.parent_id,
        title: "Home",
        icon: collection.icon,
      });
    }
